sections.css:
@import "variables/colors.scss";
@import "variables/typography.scss";
@import "variables/variables.scss";
@import "variables/spacing.scss";

$sc-neutral-shade-base: #222426;
$sc-neutral-shade-900: $sc-neutral-shade-base;
$sc-neutral-shade-600: lighten($sc-neutral-shade-base, 18);
$sc-neutral-shade-300: lighten($sc-neutral-shade-base, 55);
$sc-neutral-shade-0: lighten($sc-neutral-shade-base, 100);

$sc-color-theme-base: $sc-color-secondary;
$sc-color-theme-900: $sc-color-theme-base;
$sc-color-theme-400: desaturate(lighten($sc-color-theme-base, 45), 18);
$sc-color-theme-300: desaturate(lighten($sc-color-theme-base, 60), 22);
$sc-color-theme-200: desaturate(lighten($sc-color-theme-base, 68), 22);

$sc-color-theme-border: $sc-color-theme-400;
$sc-color-theme-border-light: $sc-color-theme-300;
$sc-color-theme-background: $sc-color-theme-200;

$sc-color-success: #097839;
$sc-color-success-background: desaturate(lighten($sc-color-success, 69), 20);
$sc-color-success-border: desaturate(lighten($sc-color-success, 61), 20);

$sc-color-warning: #946a00;
$sc-color-warning-background: desaturate(lighten($sc-color-warning, 65), 1);
$sc-color-warning-border: desaturate(lighten($sc-color-warning, 50), 20);

$sc-color-error: #c01a29;
$sc-color-error-background: lighten($sc-color-error, 54);
$sc-color-error-border: desaturate(lighten($sc-color-error, 45), 20);

$sc-color-info: $sc-color-secondary;
$sc-color-info-background: $sc-color-theme-background;
$sc-color-info-border: $sc-color-theme-border-light;

$sc-color-copy-dark: $sc-neutral-shade-base;
$sc-color-copy: lighten($sc-neutral-shade-base, 18);
$sc-color-copy-light: lighten($sc-neutral-shade-base, 35);

$sc-font-size-base: 15px;

$sc-font-size-xxs: 0.75rem;
$sc-font-size-xs: 0.87rem;
$sc-font-size-s: 0.93rem;
$sc-font-size-m: 1rem;
$sc-font-size-l: 1.067rem;
$sc-font-size-xl: 1.2rem;
$sc-font-size-xxl: 1.47rem;
$sc-font-size-xxxl: 1.73rem;

$sc-font-weight-light: 300;
$sc-font-weight-normal: 400;
$sc-font-weight-semibold: 600;
$sc-font-weight-bold: 700;

$sc-navigation-icon-size: 25px;

$sc-padding-base: 5px;
$sc-padding-lv1: $sc-padding-base;
$sc-padding-lv2: $sc-padding-base * 2;
$sc-padding-lv3: $sc-padding-base * 3;
$sc-padding-lv4: $sc-padding-base * 4;
$sc-padding-lv5: $sc-padding-base * 5;
$sc-padding-lv6: $sc-padding-base * 6;
$sc-padding-lv7: $sc-padding-base * 7;
$sc-padding-lv8: $sc-padding-base * 8;

$sc-margin-base: 5px;
$sc-margin-lv1: $sc-margin-base;
$sc-margin-lv2: $sc-margin-base * 2;
$sc-margin-lv3: $sc-margin-base * 3;
$sc-margin-lv4: $sc-margin-base * 4;
$sc-margin-lv5: $sc-margin-base * 5;
$sc-margin-lv6: $sc-margin-base * 6;
$sc-margin-lv7: $sc-margin-base * 7;
$sc-margin-lv8: $sc-margin-base * 8;

.button-primary {
	color: white;
	font-weight: $sc-font-weight-normal;
	background: $sc-color-button-primary;
	display: inline-block;
	border: 1px solid $sc-color-button-primary;
	border-radius: $sc-border-radius;

	&:hover {
		background: $sc-color-button-primary-hover;
		text-decoration: none;
		color: white;
	}

	&:focus {
		color: white;
	}

	&:active {
		background: $sc-color-button-primary-active;
		color: white;
	}

	&:disabled, &-disabled {
		cursor: default;
		pointer-events: none;
		opacity: 0.6;
	}
}

.button-secondary {
	color: white;
	font-weight: $sc-font-weight-normal;
	background: $sc-color-button-secondary;
	display: inline-block;
	border: 1px solid $sc-color-button-secondary;
	border-radius: $sc-border-radius;

	&:hover {
		background: $sc-color-button-secondary-hover;
		color: white;
		text-decoration: none;
	}

	&:active, &:focus {
		background: $sc-color-button-secondary-active;
		color: white;
	}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

.button-tertiary {
	color: $sc-color-button-tertiary;
	font-weight: $sc-font-weight-normal;
	background: $sc-neutral-shade-0;
	display: inline-block;
	border: 1px solid $sc-color-button-tertiary;
	border-radius: $sc-border-radius;

	&:hover {
		color: white;
		background: $sc-color-button-tertiary-hover;
		text-decoration: none;
	}

	&:focus {}

	&:active {}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

.button-link {
	color: $sc-color-link;
	font-weight: $sc-font-weight-normal;
	background: none;
	display: inline-block;
	border: none;
	padding: $sc-padding-lv2 0;
	margin-right: $sc-margin-lv2;

	&:hover {
		color: $sc-color-link-hover;
	}

	&:focus {
		color: $sc-color-link-hover;
	}

	&:active {
		color: $sc-color-link-hover;
	}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

.button-large {
	padding: $sc-padding-lv4 $sc-padding-lv7;
	letter-spacing: 1px;
	font-size: $sc-font-size-l;
	width: 100%;
	text-align: center;
	line-height: 1;

	@media(min-width: $screen-sm-min){
		width: auto;
	}
}

.button-medium {
	padding: $sc-padding-lv2 $sc-padding-lv4;
	letter-spacing: 0.5px;
	font-size: $sc-font-size-m;
	width: 100%;
	text-align: center;
	line-height: 1;

	@media(min-width: $screen-sm-min){
		width: auto;
	}
}

.button-small {
	padding: $sc-padding-lv2 $sc-padding-lv3;
	font-size: $sc-font-size-s;
	text-align: center;
	text-align: center;
	line-height: 1;
}

.push-button-primary {
	width: 100%;
	color: $sc-neutral-shade-0;
	background: $sc-color-secondary;
	letter-spacing: 1px;
	font-size: $sc-font-size-l;
	padding: $sc-padding-lv3;
	text-align:left;

	&:active {
		background: $sc-color-button-secondary-active;
	}

	> i {
		@extend .fa;
		@extend %fa-angle-right;
		font-size: $sc-font-size-xxl;
		float: right;
		color: $sc-neutral-shade-0;
	}

	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

.push-button-secondary {
	width: 100%;
	color: $sc-color-copy-dark;
	background: $sc-neutral-shade-0;
	font-size: $sc-font-size-s;
	padding: $sc-padding-lv3;
	border: 1px solid $sc-color-theme-border-light;
	text-align: left;
	position: relative;

	&:active {
		background: $sc-color-theme-background;
	}

	> i {
		@extend .fa;
		@extend %fa-angle-right;
		font-size: $sc-font-size-xxl;
		float: right;
		color: $sc-color-copy-dark;
		position: absolute;
		right: 15px;
		top: 50%;
		height: 26px;
		margin-top: -12px;
	}

	span {
		@extend p;
		margin-top: $sc-margin-lv3;
		color: $sc-neutral-shade-900;
		padding-right: $sc-padding-lv3;
		text-overflow: ellipsis;
		overflow: hidden;
	}

	.global-views-star-rating {
		display: inline-block;
		vertical-align: middle;
		float: none;
	}

	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

.push-button-description {
	@extend .push-button-secondary;

	p {
		margin: $sc-margin-lv3 $sc-margin-lv3 0 0;
		font-size: $sc-font-size-s;
		color: $sc-color-copy;
	}
}

.split-button {
	position: relative;
	display: inline-block;
	vertical-align: middle;

	.split-button-action {
		padding: 13px 16px;
		vertical-align: top;
		line-height: 14px;
		border-top-right-radius: 0;
		border-bottom-right-radius: 0;
		outline: none;
	}

	.split-button-toggle {
		border-left: 0;
		margin-left: -4px;
		border-top-left-radius: 0;
		border-bottom-left-radius: 0;
		outline: none;

		.split-button-toggle-icon {
			@extend .icon-angle-down;
		}

		&:hover {
			.split-button-toggle-icon {
				color: white;
			}
		}
	}

	&-dropdown {
		display:none;
		position: absolute;
		top: 100%;
		right: 0;
		z-index: 1000;
		min-width: 160px;
		text-align: left;
		list-style: none;
		background-color: $sc-neutral-shade-0;
		-webkit-background-clip: padding-box;
		background-clip: padding-box;
		border: 1px solid $sc-color-theme-border;
		border-radius: 0;
		color: $sc-color-secondary;
		margin-top: $sc-margin-lv1;
		font-size: 14px;
		@include box-shadow(0 6px 6px transparentize($sc-neutral-shade-600, 1-0.175));

		a {
			display: block;
			padding: $sc-padding-lv2;
			color: $sc-color-secondary;

			&:hover {
				color: $sc-color-link;
			}
		}

		.split-button-item-divider {
			border-top: 1px solid $sc-color-theme-border;
		}
	}
}

.open .split-button-toggle {
	@extend .button-secondary:hover;

	i.split-button-toggle-icon {
		color: $sc-neutral-shade-0;
	}
}

.open .split-button-dropdown {
	display: inline-block;
}

.pill-button {
	display: table-cell;
	border: 1px solid $sc-color-theme-border;
	color: $sc-color-secondary;
	width: auto;
	vertical-align: middle;
	line-height: 1;

	&:hover {
		background-color: $sc-color-secondary;
		border: 1px solid $sc-color-secondary;
		color: $sc-neutral-shade-0;
	}

	&-active {
		background-color: $sc-color-secondary;
		border: 1px solid $sc-color-secondary;
		color: $sc-neutral-shade-0;
	}
}

.pill-button-group {
	.pill-button:first-child,
	.pill-button-active:first-child {
		border-radius: $sc-border-radius 0 0 $sc-border-radius;
		border-right: none;
	}

	.pill-button:last-child,
	.pill-button-active:last-child {
		border-radius: 0 $sc-border-radius $sc-border-radius 0;
		border-left: none;
	}
}

.icon-button {
	background: $sc-color-primary;
	width: $sc-target-size;
	height: $sc-target-size;
	overflow: hidden;

	i {
		color: $sc-neutral-shade-0;
		width: $sc-target-size;
		height: $sc-target-size;
		font-size: $sc-font-size-xxl;
		line-height: $sc-target-size;

		@media(min-width: $screen-md-min) {
			font-size: $sc-font-size-xl;
		}
	}

	&.icon-button-primary {
		background: $sc-color-primary;

		&:hover {
			background: $sc-color-button-primary-hover;
		}

		&:active {
			background: $sc-color-button-primary-active;
		}
	}

	&.icon-button-secondary {
		background: $sc-color-secondary;

		&:hover {
			background: $sc-color-button-secondary-hover;
		}

		&:active {
			background: $sc-color-button-secondary-active;
		}
	}
}

.icon-button-controls {
	background: $sc-neutral-shade-0;
	color: $sc-color-theme-border;
	border:1px solid $sc-color-theme-border;
	text-align:center;
	display:inline-block;
	@extend .icon-button;

	&.active, .active{
		color: $sc-color-primary;
		border:1px solid $sc-color-primary;
		background: $sc-neutral-shade-0;

		i {
			color: $sc-color-primary;
		}
	}

	i {
		color: $sc-color-theme-400;
	}
}

.button-back {
	display:block;
	margin-bottom: $sc-margin-lv3;
	padding-top: $sc-padding-lv1;
	padding-bottom: $sc-padding-lv1;

	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

.button-back-icon {
	@extend .icon-base;
	@extend %fa-angle-left;
	margin-right: $sc-margin-lv1;
}

.button-positioning-stack2next {
	margin-top: $sc-margin-lv2;

	@media (min-width: $screen-sm-min) {
		margin-top: 0;
		margin-left: $sc-margin-lv1;
	}
}

.button-positioning-next2stack {
	display: inline-block;
	margin-left: $sc-margin-lv1 ;

	@media (min-width: $screen-sm-min) {
		display: block;
		margin-top: $sc-margin-lv2;
		margin-left: 0;
	}
}

.button-generic {
	width: 100%;
	text-align: center;
	line-height: 1;
	display: block; // Fix ie width 100%
	position: relative; // Fix ie width 100%
}

.button-list-header {
	height: $sc-target-size;
	line-height: $sc-target-size;
	min-width: $sc-target-size;

	padding: 0 $sc-padding-lv4;
	letter-spacing:0.5px;
	font-size: $sc-font-size-m;
	overflow: hidden;
}

.button-list-header-shrink {
	padding-left: $sc-padding-lv2;
	padding-right: $sc-padding-lv2;

	@media (min-width: $screen-sm-min){
		padding-left: $sc-padding-lv4;
		padding-right: $sc-padding-lv4;
	}
}

h1,
h2,
h3,
h4 {
    font-weight: $sc-font-weight-bold;
    text-transform: capitalize;
}

h1 {
    font-size: $sc-font-size-xxl;
    color: $sc-color-copy-dark;
    line-height: 1.2;

    @media (min-width: $screen-md-min) {
        font-size: $sc-font-size-xxxl;
    }
}

h2 {
    font-size: $sc-font-size-xl;
    color: $sc-color-copy-dark;
    line-height: 1.2;

    @media (min-width: $screen-md-min) {
        font-size: $sc-font-size-xxl;
    }
}

h3 {
    font-size: $sc-font-size-l;
    color: $sc-color-secondary;
    line-height: 1.2;

    @media (min-width: $screen-md-min) {
        font-size: $sc-font-size-xl;
    }
}

h4 {
    font-size: $sc-font-size-m;
    color: $sc-color-copy-dark;
    line-height: 1.4;

    @media (min-width: $screen-md-min) {
        font-size: $sc-font-size-l;
    }
}

h5 {
    font-size: $sc-font-size-s;
    color: $sc-color-secondary;
    line-height: 1.4;

    @media (min-width: $screen-md-min) {
        font-size: $sc-font-size-m;
    }
}

p {
    font-size: $sc-font-size-m;
    color: $sc-color-copy;
    line-height: 1.6;
}

small {
    font-size: $sc-font-size-xs;
}

b, strong {
    font-weight: $sc-font-weight-bold;
}

.large-title {
    @extend h1;
}

.large-title-navigable {
    @extend h1;
    color: $sc-color-secondary;

    :hover{
        color: $sc-color-link-hover;
        text-decoration: none;
    }
}

.large-title-viewonly {
    @extend h1;
    color: $sc-color-copy-dark;
}

.medium-title {
    font-weight: $sc-font-weight-semibold;
    font-size: $sc-font-size-l;
    color: $sc-color-copy-dark;
}

.medium-title-navigable {
    color:$sc-color-secondary;
    font-size: $sc-font-size-l;
    font-weight:$sc-font-weight-semibold;

    &:hover{
        color: $sc-color-link-hover;
        text-decoration:none;
    }
}

.medium-title-viewonly {
    font-size: $sc-font-size-l;
    color: $sc-color-copy-dark;
    font-weight:$sc-font-weight-semibold;
}

.small-title {
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-semibold;
}

.small-title-navigable {
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-semibold;
    color: $sc-color-secondary;

    :hover{
        color: $sc-color-link-hover;
        text-decoration: none;
    }
}

.small-title-viewonly {
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-semibold;
}

.xsmall-title {
    font-size: $sc-font-size-m;
    color: $sc-color-copy-dark;
    font-weight: $sc-font-weight-normal;
    text-transform: capitalize;
}

.xsmall-title-navigable {
    color: $sc-color-secondary;
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-normal;
    text-transform: capitalize;

    &:hover{
        color: $sc-color-link-hover;
        text-decoration:none;
    }
}

.xsmall-title-viewonly {
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-normal;
    text-transform: capitalize;
}

.large-price {
    @extend h1;
    color: $sc-color-copy-dark;
    font-weight: $sc-font-weight-semibold;
}

.middle-price {
    font-size: $sc-font-size-m;
    font-weight: $sc-font-weight-semibold;
    color: $sc-color-primary;
}

.small-price {
    font-size:14px;
    color: $sc-color-primary;
}

.old-price {
    font-size: $sc-font-size-xs;
    color: $sc-color-theme-900;
    font-weight: $sc-font-weight-light;
    text-decoration:line-through;
}

.sc-sku-label, .sc-sku-value {
    font-size: $sc-font-size-s;
    color: $sc-color-copy-light;
}

.sc-label {
    color: $sc-color-copy-light;
    font-size: $sc-font-size-s;
}

.sc-value {
    color: $sc-color-copy-dark;
    font-size: $sc-font-size-s;
}

.sc-highlighted {
    color: $sc-color-primary;
}

.sc-mobile-label {
    @media (min-width: $screen-sm-min){
        display: none;
    }
}

label {
	display:inline-block;
	margin-bottom: $sc-margin-lv2;
	font-size: $sc-font-size-m;
	color: $sc-color-copy-dark;

	small {
		font-size: $sc-font-size-s;

		&.required {
			color: $sc-color-primary;
		}
	}
}

// TODO: This definition should be deleted. Previously used to indicate required fields.
.input-required {
	font-size: $sc-font-size-s;
	color: $sc-color-primary;
}

// TODO: This definition should be deleted. Previously used to indicate optional fields.
.input-optional {
	font-size: $sc-font-size-s;
}

input[type="text"],
input[type="password"],
input[type="email"],
input[type="tel"],
input[type="search"],
input[type="date"],
input[type="number"]
{
	@include appearance(none);
	@include placeholder {
		color: $sc-color-copy-light;
	}
	@include border-radius(0);
	padding: $sc-padding-lv2;
	display: block;
	color: $sc-color-copy;
	font-size: $sc-font-size-m;
	border: 1px solid $sc-color-theme-border;
	width: 100%;
	margin-bottom: $sc-margin-lv2;

	&:disabled {
		border-color: $sc-color-theme-border-light;
		background-color: $sc-color-theme-background;
	}
}

input[type="checkbox"],
input[type="radio"] {
	display: inline-block;
	width: auto;
	margin-right: $sc-margin-lv2;
}

input[type="date"]
{
	-webkit-appearance: none;
}

input[type="date"]::-webkit-outer-spin-button, /* Removes arrows */
input[type="date"]::-webkit-inner-spin-button, /* Removes arrows */
input[type="date"]::-webkit-clear-button, /* Removes blue cross */
input[type="date"]::-webkit-calendar-picker-indicator /* Removes arrow calendar */ {
  -webkit-appearance: none;
  display: none;
}

input.input-large, select.input-large {
	width: auto;

	@media (min-width: $screen-md-min) {
		min-width: 300px;
	}
}

input.input-medium, select.input-medium {
	width: auto;
	min-width: 200px;

	@media (min-width: $screen-sm-min) {
		min-width: 300px;
	}
}

input.input-small, select.input-small {
	width: auto;
	min-width: 80px;
}

select {
	@include angle-down-background($sc-color-theme-900); // TODO: Mixin definition should be changed in order to use chevron icon instead an image background
	@include appearance(none);
	@include border-radius(0);
	display: block;
	border: 1px solid $sc-color-theme-border;

	padding: $sc-padding-lv2;
	padding-right: $sc-padding-lv6;
	margin-bottom: $sc-margin-lv2;

	background-color: $sc-neutral-shade-0;
	background-repeat: no-repeat;
	background-size: $sc-navigation-icon-size;
	background-position: 100% 100%;

	color: $sc-neutral-shade-600;
	font-size: $sc-font-size-m;

	&:disabled {
		border:1px solid $sc-color-theme-border-light;
		background-color: $sc-color-theme-background;
	}
}

.selector {
	display: block;
	margin: $sc-margin-lv3;
	float: left;
	@media(min-width: $screen-md-min) {
		margin: 0px;
	}

	&-select {
		width: 175px;
		border: 1px solid $sc-color-theme-border-light;
		border-left: none;
		height: $sc-target-size;
		float: right;
		@media(min-width: $screen-md-min) {
			width: 233px;
		}
	}

	&-icon {
	  width: 40px;
