}

.information-box {
	width: 100%;
	border: 1px solid $sc-color-theme-border-light;
	background-color: $sc-neutral-shade-0;
}

.information-box-body {
	padding: $sc-padding-lv3;

	@media (min-width: $screen-sm-min) {
		padding: $sc-padding-lv4;
	}
}

.information-box-summary {
	padding: $sc-padding-lv2 $sc-padding-lv3;
	border-top: 1px solid $sc-color-theme-border-light;
	background-color: $sc-color-theme-background;

	@media (min-width: $screen-sm-min) {
		padding: $sc-padding-lv2 $sc-padding-lv4;
	}
}

$sc-pusher-width-xs: 100%;
$sc-pusher-width-md: 472px;

.sc-pushing {
    position:fixed;
}

// mobile only
[data-pusher="sc-pushable-xs"]{
    width: $sc-pusher-width-xs;
    right: -$sc-pusher-width-xs;
    background: $sc-neutral-shade-0;

    @include transition-transform(400ms ease);

    &.sc-pushing-reopened {
        transition: none;
    }

    height: 100%;
    position: fixed;
    top: 0;
    z-index: 10000;
    padding-left: $sc-padding-lv4;
    padding-right: $sc-padding-lv4;
    padding-bottom: $sc-padding-lv8;

    .sc-pusher-header {
        margin-left: -$sc-margin-lv4;
        margin-right: -$sc-margin-lv4;
        @media (min-width: $screen-sm-min) {
            display: none;
        }
    }
    @extend %scroll-y;
    @media (min-width: $screen-sm-min) {
        width: auto;
        height: auto;
        position: static;
        padding-left: 0;
        padding-right: 0;
    }
}

// both tablet and mobile
[data-pusher="sc-pushable-md"]{
    width: $sc-pusher-width-xs;
    right: -$sc-pusher-width-xs;
    background: $sc-neutral-shade-0;

    @include transition-transform(400ms ease);

    &.sc-pushing-reopened {
        transition: none;
    }

    height: 100%;
    position: fixed;
    top: 0;
    z-index: 10000;
    padding-left: $sc-padding-lv4;

    .sc-pusher-header {
        margin-left: -$sc-margin-lv4;
        @media (min-width: $screen-md-min) {
            display: none;
        }
    }

    @media (min-width: $screen-sm-min) {
        width: $sc-pusher-width-md;
        right: -$sc-pusher-width-md;
    }
    @extend %scroll-y;
    @media (min-width: $screen-md-min) {
        width: auto;
        height: auto;
        position: static;
        padding-left: 0;
    }
}

[data-pusher="sc-pushable-xs"][data-action="sc-pushing"]{
    @include translate3d(-$sc-pusher-width-xs, 0, 0);
    @media (min-width: $screen-sm-min){
        transform: none;
    }
}

[data-pusher="sc-pushable-md"][data-action="sc-pushing"]{
    @media (min-width: $screen-sm-min){
        @include translate3d(-$sc-pusher-width-md, 0, 0);
    }
    @include translate3d(-$sc-pusher-width-xs, 0, 0);
    @media (min-width: $screen-md-min){
        transform: none;
    }
}

$sc-push-header-height: 69px;

.sc-pusher-header {
    background: $sc-color-theme-background;
    border-bottom: 1px solid $sc-color-theme-border-light;;
    margin-bottom: $sc-margin-lv4;
    height: $sc-push-header-height;
}

.sc-pusher-header-back {
    color: $sc-color-link;
    text-transform: capitalize;
    display: inline-block;
    width:50%;
    line-height: $sc-push-header-height;
    padding-left: $sc-padding-lv2;
}
.sc-pusher-header-back:after {
    font-family: FontAwesome;
    content:"\\f053";
    float: left;
    margin-right: $sc-margin-lv2;
}

.sc-pushing-partial {
    overflow-y: hidden;
}

.main-push-overlay {

    display: none;

    @media (min-width: $screen-sm-min) and (max-width: $screen-sm-max) {
        height: 100%;
        width: 100%;
        position: fixed;
        z-index: 99;
        top: 0;
        left: 0;

        &.active {
            display: block;
        }
    }
}

.quantity-input-container {
	display: block;
}

input[type="number"].quantity-input {
 	width: 60px;
 	height: 40px;
 	text-align: right;
 	padding: $sc-margin-lv2;
	background-color: $sc-color-theme-background;
	border: 0;
	float: left;
	margin-bottom: 0;

	@media (min-width: $screen-md-min) {
		display: block;
		background: white;
		float: none;
		border: 1px solid $sc-color-theme-border;
	}
}

.quantity-input-button {
	background-color: $sc-color-theme-400;
	color: $sc-neutral-shade-0;
	border: none;
	width: 40px;
	height: 40px;
	float: left;

	&:hover, &:active  {
		background-color: $sc-color-theme-900;
	}

	&:focus {
		outline: none;
	}

	@media (min-width: $screen-md-min) {
		display: none;
	}
}

.quantity-input-button-add {
	@extend .quantity-input-button;
	border-radius: 0 $sc-border-radius $sc-border-radius 0;
}

.quantity-input-button-remove {
	@extend .quantity-input-button;
	border-radius: $sc-border-radius 0 0 $sc-border-radius;
}

.datepicker-container {
	display: inline-block;

	.datepicker-container-input {
		position: relative;

		input {
			margin: 0;
		}

		.datepicker-calendar-icon {
			@extend .fa;
			@extend %fa-calendar;
			position: absolute;
			padding: $sc-padding-lv3;
			//width: 44px;
			//height: 44px;
			top: 0;
			right: 0;
			cursor: pointer;

			@media (min-width: $screen-md-min) {
				right: 0;
				top: 0;
			}
		}

		a {
			display: none;

			.datepicker-clear-icon {
				@extend .fa;
				@extend %fa-times-circle;
				color: $sc-color-theme-400;
				font-size: $sc-font-size-l;
			}
		}
	}
}

.datepicker {
	position:absolute;
	z-index:1;
	display:none;
	left: 0;
	width: 300px;
	border:1px solid $sc-color-theme-border-light;

	box-shadow: 0 $sc-flyout-arrow-shadow-height $sc-flyout-arrow-shadow-height * 2 transparentize($sc-neutral-shade-600, 1-0.175);
	padding: $sc-padding-lv4;
	margin-top: $sc-flyout-arrow-height;
	background: $sc-neutral-shade-0;

	&:before {
		position: absolute;
		top: -$sc-flyout-arrow-height;
		left: 14px;
		display: inline-block;
		border-right: $sc-flyout-arrow-height solid transparent;
		border-bottom: $sc-flyout-arrow-height solid $sc-color-theme-border-light;
		border-left: $sc-flyout-arrow-height solid transparent;
		content: '';
	}

	&:after {
		position: absolute;
		top: -$sc-flyout-arrow-shadow-height;
		left: 15px;
		display: inline-block;
		border-right: $sc-flyout-arrow-shadow-height solid transparent;
		border-bottom: $sc-flyout-arrow-shadow-height solid $sc-neutral-shade-0;
		border-left: $sc-flyout-arrow-shadow-height solid transparent;
		content: '';
	}

	.datepicker-switch {
		width: 100%;
		cursor: pointer;
		text-align: center;
		color: $sc-color-secondary;
	}

	.day {
		cursor:pointer;
		text-align: center;
		color: $sc-color-theme-900;
		padding: $sc-padding-lv1 $sc-padding-lv2;
	}

	.day.today {
		background-color: $sc-color-theme-900;
		color: $sc-neutral-shade-0;

		&:hover {
			background-color: $sc-color-theme-400;
	  	}
	}

	.next, .prev {
		color: white;
		cursor: pointer;
		width: $sc-target-size;
		height: $sc-target-size;
		line-height: $sc-target-size;

		@extend .fa;
		font-size:20px;
		display:inline;
		&:before{
			color: $sc-color-theme-900;
		}
	}

	.prev {
		text-align: left;
		@extend %fa-angle-double-left;
	}

	.next {
		text-align: right;
		@extend %fa-angle-double-right;
	}

	.month, .year {
		cursor: pointer;
		text-align: center;
		display: block;
		width: $sc-target-size;
		height: $sc-target-size;
		line-height: $sc-target-size;
		float: left;
		margin: 1%;
	}

	.day:hover, .month:hover, .year:hover {
		background-color: $sc-color-theme-background;
	}

	.day.active {
		background-color: $sc-color-primary;
		color: $sc-neutral-shade-0;
	}

	.day.old {
		color: $sc-color-theme-400;
	}

	.day.new,
	.day.disabled,
	.month.disabled,
	.year.disabled {
		color:  $sc-color-theme-400;
	}
}

.datepicker-orient-top {
	margin-top: $sc-margin-lv2;
}

.datepicker-orient-bottom {
	margin-bottom: $sc-margin-lv2;

	&:before {
		top: initial;
		bottom: -7px;
		border-top: $sc-flyout-arrow-height solid $sc-color-theme-border-light;
		border-bottom: 0;
	}

	&:after {
		top: initial;
		bottom: -6px;
		border-top: $sc-flyout-arrow-height solid $sc-neutral-shade-0;
		border-bottom: 0;
	}
}

.datepicker-days table thead tr th {
	color: $sc-color-secondary;
}

section.css:
@import "variables/colors.scss";
@import "variables/typography.scss";
@import "variables/variables.scss";
@import "variables/spacing.scss";

$sc-neutral-shade-base: #222426;
$sc-neutral-shade-900: $sc-neutral-shade-base;
$sc-neutral-shade-600: lighten($sc-neutral-shade-base, 18);
$sc-neutral-shade-300: lighten($sc-neutral-shade-base, 55);
$sc-neutral-shade-0: lighten($sc-neutral-shade-base, 100);

$sc-color-theme-base: $sc-color-secondary;
$sc-color-theme-900: $sc-color-theme-base;
$sc-color-theme-400: desaturate(lighten($sc-color-theme-base, 45), 18);
$sc-color-theme-300: desaturate(lighten($sc-color-theme-base, 60), 22);
$sc-color-theme-200: desaturate(lighten($sc-color-theme-base, 68), 22);

$sc-color-theme-border: $sc-color-theme-400;
$sc-color-theme-border-light: $sc-color-theme-300;
$sc-color-theme-background: $sc-color-theme-200;

$sc-color-success: #097839;
$sc-color-success-background: desaturate(lighten($sc-color-success, 69), 20);
$sc-color-success-border: desaturate(lighten($sc-color-success, 61), 20);

$sc-color-warning: #946a00;
$sc-color-warning-background: desaturate(lighten($sc-color-warning, 65), 1);
$sc-color-warning-border: desaturate(lighten($sc-color-warning, 50), 20);

$sc-color-error: #c01a29;
$sc-color-error-background: lighten($sc-color-error, 54);
$sc-color-error-border: desaturate(lighten($sc-color-error, 45), 20);

$sc-color-info: $sc-color-secondary;
$sc-color-info-background: $sc-color-theme-background;
$sc-color-info-border: $sc-color-theme-border-light;

$sc-color-copy-dark: $sc-neutral-shade-base;
$sc-color-copy: lighten($sc-neutral-shade-base, 18);
$sc-color-copy-light: lighten($sc-neutral-shade-base, 35);

$sc-font-size-base: 15px;

$sc-font-size-xxs: 0.75rem;
$sc-font-size-xs: 0.87rem;
$sc-font-size-s: 0.93rem;
$sc-font-size-m: 1rem;
$sc-font-size-l: 1.067rem;
$sc-font-size-xl: 1.2rem;
$sc-font-size-xxl: 1.47rem;
$sc-font-size-xxxl: 1.73rem;

$sc-font-weight-light: 300;
$sc-font-weight-normal: 400;
$sc-font-weight-semibold: 600;
$sc-font-weight-bold: 700;

$sc-navigation-icon-size: 25px;

$sc-padding-base: 5px;
$sc-padding-lv1: $sc-padding-base;
$sc-padding-lv2: $sc-padding-base * 2;
$sc-padding-lv3: $sc-padding-base * 3;
$sc-padding-lv4: $sc-padding-base * 4;
$sc-padding-lv5: $sc-padding-base * 5;
$sc-padding-lv6: $sc-padding-base * 6;
$sc-padding-lv7: $sc-padding-base * 7;
$sc-padding-lv8: $sc-padding-base * 8;

$sc-margin-base: 5px;
$sc-margin-lv1: $sc-margin-base;
$sc-margin-lv2: $sc-margin-base * 2;
$sc-margin-lv3: $sc-margin-base * 3;
$sc-margin-lv4: $sc-margin-base * 4;
$sc-margin-lv5: $sc-margin-base * 5;
$sc-margin-lv6: $sc-margin-base * 6;
$sc-margin-lv7: $sc-margin-base * 7;
$sc-margin-lv8: $sc-margin-base * 8;

.button-primary {
	color: white;
	font-weight: $sc-font-weight-normal;
	background: $sc-color-button-primary;
	display: inline-block;
	border: 1px solid $sc-color-button-primary;
	border-radius: $sc-border-radius;

	&:hover {
		background: $sc-color-button-primary-hover;
		text-decoration: none;
		color: white;
	}

	&:focus {
		color: white;
	}

	&:active {
		background: $sc-color-button-primary-active;
		color: white;
	}

	&:disabled, &-disabled {
		cursor: default;
		pointer-events: none;
		opacity: 0.6;
	}
}

.button-secondary {
	color: white;
	font-weight: $sc-font-weight-normal;
	background: $sc-color-button-secondary;
	display: inline-block;
	border: 1px solid $sc-color-button-secondary;
	border-radius: $sc-border-radius;

	&:hover {
		background: $sc-color-button-secondary-hover;
		color: white;
		text-decoration: none;
	}

	&:active, &:focus {
		background: $sc-color-button-secondary-active;
		color: white;
	}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

.button-tertiary {
	color: $sc-color-button-tertiary;
	font-weight: $sc-font-weight-normal;
	background: $sc-neutral-shade-0;
	display: inline-block;
	border: 1px solid $sc-color-button-tertiary;
	border-radius: $sc-border-radius;

	&:hover {
		color: white;
		background: $sc-color-button-tertiary-hover;
		text-decoration: none;
	}

	&:focus {}

	&:active {}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

.button-link {
	color: $sc-color-link;
	font-weight: $sc-font-weight-normal;
	background: none;
	display: inline-block;
	border: none;
	padding: $sc-padding-lv2 0;
	margin-right: $sc-margin-lv2;

	&:hover {
		color: $sc-color-link-hover;
	}

	&:focus {
		color: $sc-color-link-hover;
	}

	&:active {
		color: $sc-color-link-hover;
	}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

.button-large {
	padding: $sc-padding-lv4 $sc-padding-lv7;
	letter-spacing: 1px;
	font-size: $sc-font-size-l;
	width: 100%;
	text-align: center;
	line-height: 1;

	@media(min-width: $screen-sm-min){
		width: auto;
	}
}

.button-medium {
	padding: $sc-padding-lv2 $sc-padding-lv4;
	letter-spacing: 0.5px;
	font-size: $sc-font-size-m;
	width: 100%;
	text-align: center;
	line-height: 1;

	@media(min-width: $screen-sm-min){
		width: auto;
	}
}

.button-small {
	padding: $sc-padding-lv2 $sc-padding-lv3;
	font-size: $sc-font-size-s;
	text-align: center;
	text-align: center;
	line-height: 1;
}

.push-button-primary {
	width: 100%;
	color: $sc-neutral-shade-0;
	background: $sc-color-secondary;
	letter-spacing: 1px;
	font-size: $sc-font-size-l;
	padding: $sc-padding-lv3;
	text-align:left;

	&:active {
		background: $sc-color-button-secondary-active;
	}

	> i {
		@extend .fa;
		@extend %fa-angle-right;
		font-size: $sc-font-size-xxl;
		float: right;
		color: $sc-neutral-shade-0;
	}

	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

.push-button-secondary {
	width: 100%;
	color: $sc-color-copy-dark;
	background: $sc-neutral-shade-0;
	font-size: $sc-font-size-s;
	padding: $sc-padding-lv3;
	border: 1px solid $sc-color-theme-border-light;
	text-align: left;
	position: relative;

	&:active {
		background: $sc-color-theme-background;
	}

	> i {
		@extend .fa;
		@extend %fa-angle-right;
		font-size: $sc-font-size-xxl;
		float: right;
		color: $sc-color-copy-dark;
		position: absolute;
		right: 15px;
		top: 50%;
		height: 26px;
		margin-top: -12px;
	}

	span {
		@extend p;
		margin-top: $sc-margin-lv3;
		color: $sc-neutral-shade-900;
		padding-right: $sc-padding-lv3;
		text-overflow: ellipsis;
		overflow: hidden;
	}

	.global-views-star-rating {
		display: inline-block;
		vertical-align: middle;
		float: none;
	}

	@media (min-width: $screen-sm-min) {
		display: none;
	}
}

.push-button-description {
	@extend .push-button-secondary;

	p {
		margin: $sc-margin-lv3 $sc-margin-lv3 0 0;
		font-size: $sc-font-size-s;
		color: $sc-color-copy;
	}
}

.split-button {
	position: relative;
	display: inline-block;
	vertical-align: middle;

	.split-button-action {
		padding: 13px 16px;
		vertical-align: top;
		line-height: 14px;
		border-top-right-radius: 0;
		border-bottom-right-radius: 0;
		outline: none;
	}

	.split-button-toggle {
		border-left: 0;
		margin-left: -4px;
		border-top-left-radius: 0;
		border-bottom-left-radius: 0;
		outline: none;

		.split-button-toggle-icon {
			@extend .icon-angle-down;
		}

		&:hover {
			.split-button-toggle-icon {
				color: white;
			}
		}
	}

	&-dropdown {
		display:none;
		position: absolute;
		top: 100%;
		right: 0;
		z-index: 1000;
		min-width: 160px;
		text-align: left;
		list-style: none;
		background-color: $sc-neutral-shade-0;
		-webkit-background-clip: padding-box;
		background-clip: padding-box;
		border: 1px solid $sc-color-theme-border;
		border-radius: 0;
		color: $sc-color-secondary;
		margin-top: $sc-margin-lv1;
		font-size: 14px;
		@include box-shadow(0 6px 6px transparentize($sc-neutral-shade-600, 1-0.175));

		a {
			display: block;
			padding: $sc-padding-lv2;
			color: $sc-color-secondary;

			&:hover {
				color: $sc-color-link;
			}
		}

		.split-button-item-divider {
			border-top: 1px solid $sc-color-theme-border;
		}
	}
}

.open .split-button-toggle {
	@extend .button-secondary:hover;

	i.split-button-toggle-icon {
		color: $sc-neutral-shade-0;
	}
}

.open .split-button-dropdown {
	display: inline-block;
}

.pill-button {
	display: table-cell;
	border: 1px solid $sc-color-theme-border;
	color: $sc-color-secondary;
	width: auto;
	vertical-align: middle;
	line-height: 1;

	&:hover {
		background-color: $sc-color-secondary;
		border: 1px solid $sc-color-secondary;
		color: $sc-neutral-shade-0;
	}

	&-active {
		background-color: $sc-color-secondary;
		border: 1px solid $sc-color-secondary;
		color: $sc-neutral-shade-0;
	}
}

.pill-button-group {
	.pill-button:first-child,
	.pill-button-active:first-child {
		border-radius: $sc-border-radius 0 0 $sc-border-radius;
		border-right: none;
	}

	.pill-button:last-child,
	.pill-button-active:last-child {
		border-radius: 0 $sc-border-radius $sc-border-radius 0;
		border-left: none;
	}
}

.icon-button {
	background: $sc-color-primary;
	width: $sc-target-size;
	height: $sc-target-size;
	overflow: hidden;

	i {
		color: $sc-neutral-shade-0;
		width: $sc-target-size;
		height: $sc-target-size;
		font-size: $sc-font-size-xxl;
		line-height: $sc-target-size;

		@media(min-width: $screen-md-min) {
			font-size: $sc-font-size-xl;
		}
	}

	&.icon-button-primary {
		background: $sc-color-primary;

		&:hover {
			background: $sc-color-button-primary-hover;
		}

		&:active {
			background: $sc-color-button-primary-active;
		}
	}

	&.icon-button-secondary {
		background: $sc-color-secondary;

		&:hover {
			background: $sc-color-button-secondary-hover;
		}

		&:active {
			background: $sc-color-button-secondary-active;
		}
	}
}

.icon-button-controls {
	background: $sc-neutral-shade-0;
	color: $sc-color-theme-border;
	border:1px solid $sc-color-theme-border;
	text-align:center;
	display:inline-block;
	@extend .icon-button;

	&.active, .active{
		color: $sc-color-primary;
		border:1px solid $sc-color-primary;
		background: $sc-neutral-shade-0;

		i {
			color: $sc-color-primary;
		}
	}

	i {
		color: $sc-color-theme-400;
	}
}

.button-back {
