	&:hover {
		border:2px solid $sc-neutral-shade-900;
		color: $sc-neutral-shade-900;
	}
}

.picker-label.picker-checked {
	border:2px solid $sc-neutral-shade-900;
	color: $sc-neutral-shade-900;
}

%picker-color-active-hover {
	content: ' ';
	display: block;
	position: absolute;
	border: solid 2px #ffffff;
	z-index: 10;
}

%picker-color-disabled {
	content: '\2715';
	border: 0;
	display: block;
	position: absolute;
	z-index: 10;
	color: #ffffff;
	top: 0;
	left: 0;
	text-align: center;
	&.white-border{
		color: $sc-color-theme-400;
		border: 0;
		top: 0;
		left: 0;
	}
}

.picker-color {
	display:inline-block;
	margin-right: $sc-margin-lv1;
}

.picker-color-label {
	cursor: pointer;
	display: block;
}

.picker-color-input {
	opacity: 0;
	outline: 0;
	position: absolute;
}

.picker-color-box {
	display: block;
	position: relative;
	line-height: 40px;

	&:hover:before,
	&.picker-color-box-active:before {
		@extend %picker-color-active-hover;
		top: 3px;
		left: 3px;
	}

	&.picker-color-box-disabled {
		opacity: 0.4;

		&:hover:before,
		&:before {
			@extend %picker-color-disabled;
			font-size: 30px;
		}
	}

	&.picker-color-box-white-border {
		border: 1px solid #c7dde9;

		&:hover:before {
			border: solid 2px #c7dde9;
			top: 2px;
			left: 2px;
		}

		&:hover:before,
		&.picker-color-box-active:before {
			border:2px solid $sc-color-theme-border-light;
			top: 2px;
			left: 2px;
		}

		&.picker-color-box-disabled:hover:before,
		&.picker-color-box-disabled:before {
			color: $sc-color-theme-400;
			border: none;
		}
	}
}

.picker-color-lg {
	.picker-color-box {
		height: $picker-color-large;
		width: $picker-color-large;

		&:hover:before,
		&.picker-color-box-active:before {
			height: 30px;
			width: 30px;
		}
		&.picker-color-box-disabled {
			&:hover:before,
			&:before {
				@extend %picker-color-disabled;
				width: $picker-color-large;
				height: $picker-color-large;
				line-height: $picker-color-large;
			}
		}
	}
}

.picker-color-md {
	.picker-color-box {
		height: $picker-color-medium;
		width: $picker-color-medium;
	    line-height: $picker-color-medium;

		&:hover:before,
		&.picker-color-box-active:before {
			height: 20px;
			width: 20px;
		}
		&.picker-color-box-disabled {
			&:hover:before,
			&:before {
				@extend %picker-color-disabled;
				width: $picker-color-medium;
				height: $picker-color-medium;
				font-size: 25px;
				line-height: $picker-color-medium;
			}
		}
	}
}

.picker-color-sm {
	.picker-color-box {
		height: $picker-color-small;
		width: $picker-color-small;

		&:hover:before,
		&:before {
			border: none;
		}
	}
}

.picker-color-xs {
	.picker-color-box {
		height: $picker-color-xsmall;
		width: $picker-color-xsmall;

		&:before {
			border: none;
		}
	}
}

.picker-color-box-img {
	&:hover {
		outline: 2px solid #000;
		outline-offset: -2px;
	}
}

.picker-color-box-img-active {
	border: 2px solid #000;
}

.picker-color-box-img-disabled {
	opacity: 0.4;
}

%color-picker-link-hover {
	content: ' ';
	display: block;
	position: absolute;
	border: solid 2px $sc-neutral-shade-0;
	z-index: 10;
}

%color-picker-link-disabled {
	content: '\2715';
	border: 0;
	display: block;
	position: absolute;
	z-index: 10;
	color: $sc-neutral-shade-0;
	top: 0;
	left: 0;
	text-align: center;
	&.white-border{
		color: $sc-color-theme-400;
		border: 0;
		top: 0;
		left: 0;
	}
}

.color-picker {
