@import "atoms/breadcrumb.scss";
@import "atoms/stepped-process.scss";

@import "atoms/thumbnails.scss";

.breadcrumb {
	display: none;

	@media(min-width: $screen-md-min) {
		clear: both;
		margin-bottom: $sc-margin-lv6;
		display: block;
	}
}

.breadcrumb-item {
	display: inline;
}

.breadcrumb-item-active {
	@extend .breadcrumb-item;
	color: $sc-color-theme-900;
}

.breadcrumb-divider {
	display: inline;
	padding: 0 $sc-padding-lv1;
}

.breadcrumb-divider-icon {
	@extend .icon-angle-right;
	font-size: $sc-font-size-m;
}

.stepped-process {
	@include clearfix();
}

.stepped-process-button-back {
	@extend .button-back;
	float: left;
	margin-bottom: 0;
	cursor: pointer;

	> i {
		@extend .button-back-icon;
	}
}

.stepped-process-menu {
	float: right;
	position: relative;

	&:before{
		content: ' ';
		background-color: $sc-color-theme-border-light;
		height: 3px;
		display: block;
		position: absolute;
		top: 50%;
		margin-top: -1px;
		z-index: -1;
		width: 95%;
	}

	@media (min-width: $screen-sm-min) {
		float: none;

		&:before {
			display: none;
			content: none;
			height: 0;
		}
	}
}

.stepped-process-step {
	display: inline-block;
}

.stepped-process-step-anchor {
	@extend .small-title-viewonly;
	@include border-radius(100%);

	display: block;
	width: 30px; //different than $sc-target-size by design
	height: 30px;
	border: 3px solid transparent;
	line-height: 24px;
	margin-right: 3px;
	text-align: center;
	background-color: $sc-color-theme-background;

	@media (min-width: $screen-sm-min){
		width: auto;
		height: auto;
		background-color: $sc-neutral-shade-0;
	}
}

.stepped-process-step-active {
	background-color: $sc-color-theme-900;
	color: $sc-neutral-shade-0;
	cursor: default;

	&:hover {
		color: $sc-neutral-shade-0;
	}

	&:visited, &:focus {
		color: $sc-color-copy;
	}

	@media (min-width: $screen-sm-min) {
		color: $sc-color-copy;
		background-color: transparent;

		&:hover, &:focus {
			color: $sc-color-copy;
		}
	}
}

.stepped-process-step-disabled {
	border-color: $sc-color-theme-border-light;
	background-color: $sc-neutral-shade-0;
	color: $sc-color-theme-400;
	cursor: default;

	&:hover, &:visited, &:focus{
		color: $sc-color-theme-400;
	}

	@media (min-width: $screen-sm-min) {
		border-color: transparent;
	}
}

.stepped-process-divider,
.stepped-process-step-anchor-label {
	display: none;

	@media (min-width: $screen-sm-min) {
		display: inline-block;
	}
}

.stepped-process-divider {
	color: $sc-color-theme-400;
}

$sc-image-large-size: 150px;
$sc-image-medium-size: 100px;
$sc-image-small-size: 60px;

.thumbnail-large {
	width: $sc-image-large-size;
