@import "molecules/accordions.scss";
@import "molecules/flyout.scss";
@import "molecules/image-gallery.scss";
@import "molecules/information_cards.scss";
@import "molecules/information-box.scss";
@import "molecules/item-cells.scss";
@import "molecules/list-header.scss";
@import "molecules/pagination.scss";
@import "molecules/pusher.scss";
@import "molecules/search.scss";
@import "molecules/summary_box.scss";
@import "molecules/tabs.scss";
@import "molecules/_quantity-input.scss";

.accordion-head {
	@extend .small-title;
	text-transform: inherit;
}

.accordion-head-toggle,
.accordion-head-toggle-secondary {
	display: block;
	cursor: pointer;
	height: 100%;
	line-height: $sc-target-size;
	padding: 0 $sc-padding-lv4;
	font-size: $sc-font-size-m;
	border: 1px solid $sc-color-theme-border;
}

.accordion-head-toggle {
	color: $sc-color-secondary;
	font-weight: $sc-font-weight-bold;
}

.accordion-head-toggle-secondary {
	color: $sc-color-copy;
}

.accordion-head-toggle:hover,
.accordion-head-toggle-secondary:hover {
	text-decoration: none;
	color: $sc-color-copy-dark;
}

.acordion-head-toggle-icon,
.accordion-toggle-icon,
.accordion-toggle-icon-secondary {
	@extend .fa;
	@extend %fa-angle-up;
	float: right;
	height: $sc-target-size;
	line-height: $sc-target-size;
	font-size: $sc-font-size-xl;
	color: $sc-color-secondary;
	@include transition (transform $sc-animation-base-duration ease)
}

.accordion-toggle-icon-secondary {
	color: $sc-color-copy;
}

.collapsed .acordion-head-toggle-icon,
.collapsed .accordion-toggle-icon,
.collapsed .accordion-toggle-icon-secondary {
	@include rotate(180deg);
}

.accordion-body,
.accordion-body-secondary,
.accordion-body-alt {
	overflow: hidden;
	background-color: $sc-neutral-shade-0;
	border: 1px solid $sc-color-theme-border;
	border-top: 0;
	width: 100%;
}

.accordion-body-alt {
	background-color: $sc-color-theme-background;
}

.accordion-container {
	margin: $sc-margin-lv4;
}

.accordion-container-table {
	margin: 0;
}

.accordion-divider {
	margin-bottom: $sc-margin-lv4;
}

.sc-flyout {
	display: inline-block;
	position: relative;
	vertical-align: middle;
}

.sc-flyout-control {
	@extend .button-small;
	color: $sc-color-button-tertiary;
	padding: 0 $sc-padding-lv3;
	font-weight: $sc-font-weight-semibold;
	border: 1px solid $sc-color-theme-border-light;
	height: $sc-target-size;
	line-height: $sc-target-size;
	text-align: center;
	cursor: pointer;
	background: white;
	border-radius: $sc-border-radius;

	&:hover {
		color: $sc-color-button-tertiary-hover;
		text-decoration: none;

		&:disabled{
			opacity: 0.6;
			color: $sc-color-button-tertiary;
		}
	}

	&:focus {
		color: $sc-color-button-tertiary;
		outline: none;

		&:hover{
			color: $sc-color-button-tertiary-hover;
		}
	}

	&:active{
		color: $sc-color-button-tertiary-active;
	}

	&:disabled {
		opacity: 0.6;
		cursor: default;
	}
}

.sc-flyout-toggle-icon {
	@extend .fa;
	@extend %fa-angle-down;
	float: right;
	height: $sc-target-size;
	line-height: $sc-target-size;
	font-size: $sc-font-size-l;
	color: $sc-color-secondary;
	margin-left: $sc-margin-lv2;
	@include transition (transform $sc-animation-base-duration ease);
}

.sc-flyout-content {
	@extend .dropdown-menu;
	@include transition (opacity $sc-animation-base-duration ease);

	display: none;
	opacity: 0;
	border: 1px solid $sc-color-theme-border-light;
	box-shadow: 0 6px 6px transparentize($sc-neutral-shade-600, 1-0.175);
	left: 0;
	padding: $sc-padding-lv3;
	margin-top: 7px;
	background: $sc-neutral-shade-0;
	position:absolute;
	z-index:1;

	&:before {
		position: absolute;
		top: -7px;
		left: 14px;
		display: inline-block;
		border-right: 7px solid transparent;
		border-bottom: 7px solid $sc-color-theme-border-light;
		border-left: 7px solid transparent;
		border-bottom-color: transparentize($sc-neutral-shade-600, 1-0.2);
		content: '';
	}

	&:after {
		position: absolute;
		top: -6px;
		left: 15px;
		display: inline-block;
		border-right: 6px solid transparent;
		border-bottom: 6px solid $sc-neutral-shade-0;
		border-left: 6px solid transparent;
		content: '';
	}
}

.open .sc-flyout-content {
	display: block;
	opacity: 1;
}

.open .sc-flyout-control {
	@extend .button-tertiary:hover;
}

.open .sc-flyout-toggle-icon {
	@include rotate(180deg);
}

.sc-flyout-bg {
	background: $sc-neutral-shade-900;
	opacity: 0.1;
	height: 100%;
	width: 100%;
	position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    filter: alpha(opacity=10);

    @media (min-width: $screen-sm-min) {
    	display: none;
    }
}

.image-gallery {
	list-style: none;
	margin: 0;
}

.image-gallery li {
	display: inline-block;
}

.image-gallery img {
    display: block;
    max-width: 100%;
    height: auto;
}

// Wrapped in HTML tag to add the smallest hevier selector
// to fix bug on IE
html {

	.bx-controls {
		margin-top: $sc-margin-lv4;
	}

	.bx-wrapper {
		margin-bottom: 0;

		.bx-viewport {
			border: none;
			-webkit-box-shadow: none;
			-moz-box-shadow: none;
			box-shadow: none;
			left: 0;
		}

		.bx-pager {

			text-align: center;
			margin-top: $sc-margin-lv4;

			.touch & {
				overflow-x: scroll;
				-webkit-overflow-scrolling: touch;
				white-space: nowrap;
			}

			a {
				display: block;
				width:10px;
				height: 10px;
				border-radius:100%;
				opacity: 0.5;

				background-color: desaturate(darken($sc-color-theme-400, 13), 25);
				margin: 0 $sc-margin-lv1;
			}

			a.active{
				opacity: 1;
			}

			img{
				display: none;
			}

			@media (min-width: $screen-md-min) {
				margin-bottom: $sc-margin-lv4;
				position: static;

				img{
					display: inline;
				}

				a {
					margin: 0 8px;
					padding: 3px;

					width:	auto;
					height: auto;
					border-radius:0;
					background-color: transparent;
				}

			}
		}

		img {
			margin: 0 auto;
		}


	}

	.bx-pager-item img {
		max-width: 50px;
	}

}

