$sc-pusher-width-xs: 100%;
$sc-pusher-width-md: 472px;

.sc-pushing {
    position:fixed;
}

// mobile only
[data-pusher="sc-pushable-xs"]{
    width: $sc-pusher-width-xs;
    right: -$sc-pusher-width-xs;
    background: $sc-neutral-shade-0;

    @include transition-transform(400ms ease);

    &.sc-pushing-reopened {
        transition: none;
    }

    height: 100%;
    position: fixed;
    top: 0;
    z-index: 10000;
    padding-left: $sc-padding-lv4;
    padding-right: $sc-padding-lv4;
    padding-bottom: $sc-padding-lv8;

    .sc-pusher-header {
        margin-left: -$sc-margin-lv4;
        margin-right: -$sc-margin-lv4;
        @media (min-width: $screen-sm-min) {
            display: none;
        }
    }
    @extend %scroll-y;
    @media (min-width: $screen-sm-min) {
        width: auto;
        height: auto;
        position: static;
        padding-left: 0;
        padding-right: 0;
    }
}

// both tablet and mobile
[data-pusher="sc-pushable-md"]{
    width: $sc-pusher-width-xs;
    right: -$sc-pusher-width-xs;
    background: $sc-neutral-shade-0;

    @include transition-transform(400ms ease);

    &.sc-pushing-reopened {
        transition: none;
    }

    height: 100%;
    position: fixed;
    top: 0;
    z-index: 10000;
    padding-left: $sc-padding-lv4;

    .sc-pusher-header {
        margin-left: -$sc-margin-lv4;
        @media (min-width: $screen-md-min) {
            display: none;
        }
    }

    @media (min-width: $screen-sm-min) {
        width: $sc-pusher-width-md;
        right: -$sc-pusher-width-md;
    }
    @extend %scroll-y;
    @media (min-width: $screen-md-min) {
        width: auto;
        height: auto;
        position: static;
        padding-left: 0;
    }
}

[data-pusher="sc-pushable-xs"][data-action="sc-pushing"]{
    @include translate3d(-$sc-pusher-width-xs, 0, 0);
    @media (min-width: $screen-sm-min){
        transform: none;
    }
}

[data-pusher="sc-pushable-md"][data-action="sc-pushing"]{
    @media (min-width: $screen-sm-min){
        @include translate3d(-$sc-pusher-width-md, 0, 0);
    }
    @include translate3d(-$sc-pusher-width-xs, 0, 0);
    @media (min-width: $screen-md-min){
        transform: none;
    }
}

$sc-push-header-height: 69px;

.sc-pusher-header {
    background: $sc-color-theme-background;
    border-bottom: 1px solid $sc-color-theme-border-light;;
    margin-bottom: $sc-margin-lv4;
    height: $sc-push-header-height;
}

.sc-pusher-header-back {
    color: $sc-color-link;
    text-transform: capitalize;
    display: inline-block;
    width:50%;
    line-height: $sc-push-header-height;
    padding-left: $sc-padding-lv2;
}
.sc-pusher-header-back:after {
    font-family: FontAwesome;
    content:"\\f053";
    float: left;
    margin-right: $sc-margin-lv2;
}

.sc-pushing-partial {
    overflow-y: hidden;
}

.main-push-overlay {

    display: none;

    @media (min-width: $screen-sm-min) and (max-width: $screen-sm-max) {
        height: 100%;
        width: 100%;
        position: fixed;
        z-index: 99;
        top: 0;
        left: 0;

        &.active {
            display: block;
        }
    }
}

.quantity-input-container {
	display: block;
}

input[type="number"].quantity-input {
 	width: 60px;
 	height: 40px;
 	text-align: right;
 	padding: $sc-margin-lv2;
	background-color: $sc-color-theme-background;
	border: 0;
	float: left;
	margin-bottom: 0;

	@media (min-width: $screen-md-min) {
		display: block;
		background: white;
		float: none;
		border: 1px solid $sc-color-theme-border;
	}
}

.quantity-input-button {
	background-color: $sc-color-theme-400;
	color: $sc-neutral-shade-0;
	border: none;
	width: 40px;
	height: 40px;
	float: left;

	&:hover, &:active  {
		background-color: $sc-color-theme-900;
	}

	&:focus {
		outline: none;
	}

	@media (min-width: $screen-md-min) {
		display: none;
	}
}

.quantity-input-button-add {
	@extend .quantity-input-button;
	border-radius: 0 $sc-border-radius $sc-border-radius 0;
}

.quantity-input-button-remove {
	@extend .quantity-input-button;
	border-radius: $sc-border-radius 0 0 $sc-border-radius;
}

.datepicker-container {
	display: inline-block;

	.datepicker-container-input {
		position: relative;

		input {
			margin: 0;
		}

		.datepicker-calendar-icon {
			@extend .fa;
			@extend %fa-calendar;
			position: absolute;
			padding: $sc-padding-lv3;
			//width: 44px;
			//height: 44px;
			top: 0;
			right: 0;
			cursor: pointer;

			@media (min-width: $screen-md-min) {
				right: 0;
				top: 0;
			}
		}

		a {
			display: none;

			.datepicker-clear-icon {
				@extend .fa;
				@extend %fa-times-circle;
				color: $sc-color-theme-400;
				font-size: $sc-font-size-l;
			}
		}
	}
}

.datepicker {
	position:absolute;
	z-index:1;
	display:none;
	left: 0;
	width: 300px;
	border:1px solid $sc-color-theme-border-light;

	box-shadow: 0 $sc-flyout-arrow-shadow-height $sc-flyout-arrow-shadow-height * 2 transparentize($sc-neutral-shade-600, 1-0.175);
	padding: $sc-padding-lv4;
	margin-top: $sc-flyout-arrow-height;
	background: $sc-neutral-shade-0;

	&:before {
		position: absolute;
		top: -$sc-flyout-arrow-height;
		left: 14px;
		display: inline-block;
		border-right: $sc-flyout-arrow-height solid transparent;
		border-bottom: $sc-flyout-arrow-height solid $sc-color-theme-border-light;
		border-left: $sc-flyout-arrow-height solid transparent;
		content: '';
	}

	&:after {
		position: absolute;
		top: -$sc-flyout-arrow-shadow-height;
		left: 15px;
		display: inline-block;
		border-right: $sc-flyout-arrow-shadow-height solid transparent;
		border-bottom: $sc-flyout-arrow-shadow-height solid $sc-neutral-shade-0;
		border-left: $sc-flyout-arrow-shadow-height solid transparent;
		content: '';
	}

	.datepicker-switch {
		width: 100%;
		cursor: pointer;
		text-align: center;
		color: $sc-color-secondary;
	}

	.day {
		cursor:pointer;
		text-align: center;
		color: $sc-color-theme-900;
		padding: $sc-padding-lv1 $sc-padding-lv2;
	}

	.day.today {
		background-color: $sc-color-theme-900;
		color: $sc-neutral-shade-0;

		&:hover {
			background-color: $sc-color-theme-400;
	  	}
	}

	.next, .prev {
		color: white;
		cursor: pointer;
		width: $sc-target-size;
		height: $sc-target-size;
		line-height: $sc-target-size;

		@extend .fa;
		font-size:20px;
		display:inline;
		&:before{
			color: $sc-color-theme-900;
		}
	}

	.prev {
		text-align: left;
		@extend %fa-angle-double-left;
	}

	.next {
		text-align: right;
		@extend %fa-angle-double-right;
	}

	.month, .year {
		cursor: pointer;
		text-align: center;
		display: block;
		width: $sc-target-size;
		height: $sc-target-size;
		line-height: $sc-target-size;
		float: left;
		margin: 1%;
	}

	.day:hover, .month:hover, .year:hover {
		background-color: $sc-color-theme-background;
	}

	.day.active {
		background-color: $sc-color-primary;
		color: $sc-neutral-shade-0;
	}

	.day.old {
		color: $sc-color-theme-400;
	}

	.day.new,
	.day.disabled,
	.month.disabled,
	.year.disabled {
		color:  $sc-color-theme-400;
	}
}

.datepicker-orient-top {
	margin-top: $sc-margin-lv2;
}

.datepicker-orient-bottom {
	margin-bottom: $sc-margin-lv2;

	&:before {
		top: initial;
		bottom: -7px;
		border-top: $sc-flyout-arrow-height solid $sc-color-theme-border-light;
		border-bottom: 0;
	}

	&:after {
		top: initial;
		bottom: -6px;
		border-top: $sc-flyout-arrow-height solid $sc-neutral-shade-0;
		border-bottom: 0;
	}
}

.datepicker-days table thead tr th {
	color: $sc-color-secondary;
}
